import React from 'react';
import classNames from 'classnames';
import { SectionSplitProps } from '../../utils/SectionProps';
import SectionHeader from './partials/SectionHeader';
import Image from '../elements/Image';
import ButtonGroup from '../elements/ButtonGroup';
import Button from '../elements/Button';

const propTypes = {
  ...SectionSplitProps.types
}

const defaultProps = {
  ...SectionSplitProps.defaults
}

const FeaturesSplit = ({
  className,
  topOuterDivider,
  bottomOuterDivider,
  topDivider,
  bottomDivider,
  hasBgColor,
  invertColor,
  invertMobile,
  invertDesktop,
  alignTop,
  imageFill,
  ...props
}) => {

  const outerClasses = classNames(
    'features-split section',
    topOuterDivider && 'has-top-divider',
    bottomOuterDivider && 'has-bottom-divider',
    hasBgColor && 'has-bg-color',
    invertColor && 'invert-color',
    className
  );

  const innerClasses = classNames(
    'features-split-inner section-inner',
    topDivider && 'has-top-divider',
    bottomDivider && 'has-bottom-divider'
  );

  const splitClasses = classNames(
    'split-wrap',
    invertMobile && 'invert-mobile',
    invertDesktop && 'invert-desktop',
    alignTop && 'align-top'
  );

  const sectionHeader = {
    title: '',
    paragraph: ''
  };

  return (
    <section
      {...props}
      className={outerClasses}
    >
      <div className="container">
        <div className={innerClasses}>
          <SectionHeader data={sectionHeader} className="center-content" />
          <div className={splitClasses}>

            <div className="split-item">
              <div className="split-item-content center-content-mobile reveal-from-left" data-reveal-container=".split-item">
                <div className="text-xs text-color-primary fw-600 tt-u mb-8">
                  Rapidly accelerate your growth
                  </div>
                <h3 className="mt-0 mb-12">
                  Creators
                  </h3>
                <p className="m-0">
                  Issue your very own tokens to obtain funds needed to start or expand your content creation business.
                  Then, leverage a strong, loyal fanbase to further expand & promote your token — sending its price to the moon! 🚀
                  </p>
              </div>
              <div className={
                classNames(
                  'split-item-image center-content-mobile reveal-from-bottom',
                  imageFill && 'split-item-image-fill'
                )}
                data-reveal-container=".split-item">
                <Image
                  src={require('./../../assets/images/creator.png')}
                  alt="Features split 01"
                  width={528}
                  height={396} />
              </div>
            </div>

            <div className="split-item">
              <div className="split-item-content center-content-mobile reveal-from-right" data-reveal-container=".split-item">
                <div className="text-xs text-color-primary fw-600 tt-u mb-8">
                  Fund the next superstars
                  </div>
                <h3 className="mt-0 mb-12">
                  Investors
                  </h3>
                <p className="m-0">
                  Purchase tokens of your favorite creators to support their growth and development.
                  Then, earn a monthly dividend for each token you own, exchange some tokens for
                  exclusive perks (think merch discounts and autographs), and sell your tokens for a
                  massive profit in the future.
                  </p>
              </div>
              <div className={
                classNames(
                  'split-item-image center-content-mobile reveal-from-bottom',
                  // imageFill && 'split-item-image-fill'
                )}
                data-reveal-container=".split-item">
                <Image
                  src={require('./../../assets/images/investor.png')}
                  alt="Features split 02"
                  width={396}
                  height={297} />
              </div>
            </div>

          </div>
        </div>
          <div className="reveal-from-bottom" data-reveal-delay="600" margin="auto">
              <ButtonGroup>
                <Button tag="a" href="https://form.jotform.com/220610729544049" target="_blank" color="primary" >
                  Subscribe for early access
                  </Button>
              </ButtonGroup>
          </div>
      </div>
    </section>
  );
}

FeaturesSplit.propTypes = propTypes;
FeaturesSplit.defaultProps = defaultProps;

export default FeaturesSplit;
