import React, { useEffect, useState } from "react";
import Faq from "react-faq-component";

const data = {
    title: "FAQs",
    rows: [
        {
            title: "What is Rondevoo?",
            content: `Rondevoo is a marketplace where creators can raise money to fund their endeavors and anyone can
                      invest in their favorite creators - earning dividends and special benefits for doing so. To invest,
                      you purchase creator tokens (think of them as shares of stock) which behave just like any other 
                      financial asset.`
        },
        {
            title: "How much will this cost to use?",
            content: "For now, it's completely free. Eventually, we will charge a low fee, and will make you aware of this well ahead of time.",
        },
        {
            title: "What determines the price of a creator token?",
            content: `When a creator first generates/issues a set number of tokens, they set the initial price per token based on how 
                      much money they want to raise. Then, market supply and demand for the token take over, causing the price 
                      per token to change over time.`
        },
        {
            title: "How are dividends and special benefits determined?",
            content: `Creators set the monthly dividend amount and the exclusive perks that its investors are entitled to. You can
                      even redeem some of your tokens to access extra special perks from the creator.`
        },

    ],
};

const styles = {
    bgColor: "rgba(52,52,52,alpha)",
    titleTextColor: "white",
    rowTitleColor: "rgba(124,66,234,1)",
    rowContentColor: "rgba(154,161,169,1)",
    arrowColor: "white",
};

const config = {
    animate: true,
    arrowIcon: "V",
    tabFocus: true
};

export default function App() {

    return (
        <div style={{maxWidth:800, display:"flex", width: "100%", justifyContent: "center", margin:"auto"}}>
            <br />
            <Faq
                data={data}
                styles={styles}
                config={config}
            />
        </div>
    );
}
