import React from "react";
import Faq from "react-faq-component";

const data = {
    title: "FAQs",
    rows: [
        {
            title: "What is Rondevoo?",
            content: `Rondevoo is a marketplace where anyone with a talent can raise money to fund their endeavors and anyone can
                      invest in their favorite talent — earning exclusive perks for doing so. To invest,
                      you purchase tokens (think of them as shares of stock) which behave just like any other 
                      financial asset.`
        },
        {
            title: "How much will this cost to use?",
            content: "For now, it's completely free. Eventually, we'll charge a low fee and we'll be sure to make you aware of this change well ahead of time.",
        },
        {
            title: "What determines the price of a creator's token?",
            content: `When an athlete, creator, etc. first generates/issues a set number of tokens, they set the initial price per token based on how 
                      much money they want to raise. Then, the  and investors can buy and sell more tokens via an auction provided on Rondevoo,
                      letting supply and demand take over.`
        },
        {
            title: "How are the exclusive perks determined?",
            content: `The talent decides on the exclusive perks that its investors are entitled to, and these will be communicated
                      via a private Discord server that investors can join. Later, some members of our talent community may even offer
                      a monthly dividend (cash payment) to their investors!`
        }
    ],
};

const styles = {
    bgColor: "rgba(52,52,52,alpha)",
    titleTextColor: "white",
    rowTitleColor: "rgba(124,66,234,1)",
    rowContentColor: "rgba(154,161,169,1)",
    arrowColor: "white",
};

const config = {
    animate: true,
    arrowIcon: "V",
    tabFocus: true
};

export default function App() {

    return (
        <div style={{maxWidth:800, display:"flex", width: "100%", justifyContent: "center", margin:"auto"}}>
            <br />
            <Faq
                data={data}
                styles={styles}
                config={config}
            />
        </div>
    );
}
